import { Component } from '@angular/core';
import { StudentInfoService } from 'src/shared/service/student/student-info.service';

@Component({
  selector: 'app-add-concentration-course',
  templateUrl: './add-concentration-course.component.html',
  styleUrls: ['./add-concentration-course.component.css']
})
export class AddConcentrationCourseComponent {
  showConcentrationForm = false;
  showCourseForm = false;
  concentrationName: string;
  courseId: string;
  courseName: string;
  selectedConcentration: number;
  courseHours: number;
  concentrations = {};
  alertMessage: string;
  concentrationAlertMessage: string; 
 // This should be populated with real data

 constructor(private studentInfoService: StudentInfoService) {}

  toggleForm(formType: string): void {
    this.showConcentrationForm = formType === 'concentration';
    this.showCourseForm = formType === 'course';
  }

  ngOnInit(): void {
    this.studentInfoService.getConcentrations().subscribe(data => {
      this.concentrations = data;
    });
  }

  submitConcentration(): void {
    // Check if concentrationName is not empty
    if (!this.concentrationName) {
      console.error("Concentration Name is required.");
      return; // Exit the method if concentrationName is empty
    }

    // Call the service to add the concentration
    this.studentInfoService.addConcentration(this.concentrationName, "admin").subscribe
      ({
        next: response => {
          this.concentrationAlertMessage  = 'Concentration added successfully';
        },
        error: error => {
          this.concentrationAlertMessage  = error.message;
            console.error('There was an error!', error);
        }
    });
  }

  showForm(formType: string): void {
    this.showConcentrationForm = formType === 'concentration';
    this.showCourseForm = formType === 'course';
  }
  keys(obj: any): string[] {
    return Object.keys(obj);
  }

  submitCourse(): void {
    console.log("Course Name:", this.courseName, "Concentration:", this.selectedConcentration);

    // Prepare the course data
    const courseData = {
      courseId: this.courseId,
      courseName: this.courseName,
      concentrationCode: this.selectedConcentration,
      hours: this.courseHours,
      createdBy: "admin" // Assuming a default value for createdBy
    };

    // Call the service method to add the course
    this.studentInfoService.addCourse(courseData).subscribe
    ({
      next: response => {
        this.alertMessage = response;
      },
      error: error => {
          this.alertMessage = error.message;
          console.error('There was an error!', error);
      }
  });
  }
  }
<div class="form-container">
    <h2>Add New Concentration or Course</h2>

    <div class="radio-group">
        <label>
            <input type="radio" name="choice" (click)="toggleForm('concentration')"> Add Concentration
        </label>
        <label>
            <input type="radio" name="choice" (click)="toggleForm('course')"> Add Course
        </label>
    </div>

    <div *ngIf="showConcentrationForm" class="form-section">
        <h3>Add Concentration</h3>
        <input type="text" [(ngModel)]="concentrationName" placeholder="Enter Concentration Name">
        <button (click)="submitConcentration()">Submit Concentration</button>

        <div *ngIf="concentrationAlertMessage" class="salert success-alert" role="alert">
            {{ concentrationAlertMessage }}
        </div>
    </div>



    <div *ngIf="showCourseForm" class="form-section">
        <h3>Add Course</h3>
        <label for="courseIdInput">Course ID:</label>
        <input type="text" id="courseIdInput" [(ngModel)]="courseId" placeholder="Enter Course ID">

        <label for="courseNameInput">Course Name:</label>
        <input type="text" id="courseNameInput" [(ngModel)]="courseName" placeholder="Enter Course Name">

        <label for="concentrationSelect">Select Concentration:</label>
        <select id="concentrationSelect" [(ngModel)]="selectedConcentration">
            <option *ngFor="let key of keys(concentrations)" [value]="key">
                {{ concentrations[key] }}
            </option>
        </select>

        <label for="hoursInput">Hours:</label>
        <input type="number" id="hoursInput" [(ngModel)]="courseHours" placeholder="Enter Course Hours">

        <button (click)="submitCourse()">Submit Course</button>

        <div *ngIf="alertMessage" class="alert success-alert" role="alert">
            {{ alertMessage }}
          </div>
    </div>
</div>
